import React from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import './Product.css';

const Sidebar = () => {
  return (
    <div className="sidebar">
      {/* Sidebar content */}
      <ul>
        <li><Link to="/card/1">Row 1</Link></li>
        <li><Link to="/card/2">Row 2</Link></li>
        <li><Link to="/card/3">Row 3</Link></li>
        <li><Link to="/card/4">Row 4</Link></li>
        <li><Link to="/card/5">Row 5</Link></li>
        <li><Link to="/card/6">Row 6</Link></li>
        <li><Link to="/card/7">Row 7</Link></li>
      </ul>
    </div>
  );
};

const CardPage = ({ match }) => {
  const cardId = match.params.cardId;

  // Replace this with actual card data based on the cardId
  const cardData = {
    id: cardId,
    title: `Card ${cardId} Title`,
    image: `path-to-card-${cardId}-image.jpg`,
    description: `This is the description for Card ${cardId}.`,
  };

  return (
    <div className="main-content">
      {/* Card with image and title */}
      <div className="card">
        <img src={cardData.image} alt={`Card ${cardId} Image`} />
        <h2>{cardData.title}</h2>
        <p>{cardData.description}</p>
      </div>
    </div>
  );
};

const Products = () => {
  return (
    <div className="page-container">
      <Sidebar />

      <Routes>
        <Route path="/card/:cardId" component={CardPage} />
      </Routes>
    </div>
  );
};

export default Products;
